import { StatusBar } from 'expo-status-bar';
import * as React from 'react';
import { Button, StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import Login from './src/components/login';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import axios from 'axios'
import './node_modules/bootstrap/dist/css/bootstrap.min.css'

function App() {
  return (
    // <View style={styles.container}>
    //   <Login />
    // </View>
    <NavigationContainer>
      <Stack.Navigator>

        <Stack.Screen name="Los Hermanos" component={Login} />
        <Stack.Screen name="Cardápio" component={HomeScreen} />

      </Stack.Navigator>
    </NavigationContainer>
  );
}
// Cardápio
function HomeScreen({ navigation }) {
  const [data, setData] = React.useState([])

  React.useEffect(() => {
    axios.get('https://api.mercadolibre.com/sites/MLB/search?q=celular').then(res => {
      console.log(res.data.results)
      setData(res.data.results)
    })
  }, [])
  return (

    <View style={styles.container}>
      {
        data.map(item => <div key={item.id}>
          <div className='row border border-1 border-solid'>
          <TouchableOpacity>
          <img src={item.thumbnail} alt="" className='col-sm-6 p-2'/>
          <h4 className='col-sm-6 p-2 m-2'>{item.title}</h4>
          </TouchableOpacity>

          </div>
        </div>)
      }
    </View>
  );
}

const Stack = createNativeStackNavigator();

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection:'row'
  },
});
