import React, { useState } from "react";
import { View, Text, TextInput, Touchable, TouchableOpacity, StyleSheet, Button } from "react-native";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import styles from "../../../estiloGeral";
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css'

export default function Carrinho({ navigation }) {
    return (
        <View style={styles.container}>
            <View style={estilo.main}>

            </View>
            <View style={estilo.secon}>
                <Text style={styles.detalheTexto}>Valor total:</Text>
                <View style={estilo.flexend}>
                    <View style={{ width: '50%', height: '100%' }}>
                        <Button title="enviar" buttonStyle={{ backgroundColor: '#17a2b8' }}
  titleStyle={{ color: 'white', fontWeight: 'bold' }}></Button>
                    </View>
                    <View style={{ width: '50%', height: '100%' }}>
                        <Button title="finalizar" buttonStyle={{ backgroundColor: '#17a2b8' }}
  titleStyle={{ color: 'white', fontWeight: 'bold' }}></Button>
                    </View>
                </View>
                
                {/* <Button title="finalizar" style={[estilo.botoes, {width:'50%'}]}></Button> */}
            </View>
        </View>
    )
}
const estilo = StyleSheet.create({
    main: {
        flex: 5,
        backgroundColor: '#2c2c2c'
    },
    flexend: {
        alignContent:'flex-end',
        flexDirection: 'row',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
    },
    secon: {
        flex: 1,
        
    },
    botoes: {
        padding: 5,
        margin: 5,
        // height:'100%'

    }

})
